from mpi4py import MPIimport timedef pi(num_steps, myrank, nprocs):    step = 1.0 / num_steps    sum = 0.0    for i in range(myrank, num_steps, nprocs):        x = step * (i + 0.5)        sum += 4.0 / (1.0 + x**2)    return step * sumcomm = MPI.COMM_WORLDnprocs = comm.Get_size()myrank = comm.Get_rank()if myrank == 0:    start=MPI.Wtime()start = time.time()N = 10000000mypi = pi(N, myrank, nprocs)pi = comm.reduce(mypi, op=MPI.SUM, root=0)end = time.time()if myrank == 0:    print(f"Pi = {pi}, Time = ", (end-start), "Sec")        